<body>
    <h1>2048 Game</h1>
    <div class="header">
        <div class="score-container">
            Score: <span id="score">0</span>
        </div>
        <div class="score-container">
            High Score: <span id="high-score">0</span>
        </div>
    </div>
    <div class="game-container">
        <div class="grid" id="grid"></div>
    </div>
    <button id="restart">Restart Game</button>
    <script>
        const grid = document.getElementById('grid');
        const size = 4;
        let board = Array(size).fill().map(() => Array(size).fill(0));
        let score = 0;
        let highScore = localStorage.getItem('highScore') || 0;
        document.getElementById('high-score').textContent = highScore;

        function initializeBoard() {
            for (let i = 0; i < size; i++) {
                for (let j = 0; j < size; j++) {
                    const cell = document.createElement('div');
                    cell.className = 'cell';
                    cell.id = `cell-${i}-${j}`;
                    grid.appendChild(cell);
                }
            }
            addRandomTile();
            addRandomTile();
            updateBoard();
        }

        function addRandomTile() {
            const emptyCells = [];
            for (let i = 0; i < size; i++) {
                for (let j = 0; j < size; j++) {
                    if (board[i][j] === 0) emptyCells.push({ i, j });
                }
            }
            if (emptyCells.length > 0) {
                const { i, j } = emptyCells[Math.floor(Math.random() * emptyCells.length)];
                board[i][j] = Math.random() < 0.9 ? 2 : 4;
            }
        }

        function updateBoard() {
            for (let i = 0; i < size; i++) {
                for (let j = 0; j < size; j++) {
                    const cell = document.getElementById(`cell-${i}-${j}`);
                    cell.textContent = board[i][j] === 0 ? '' : board[i][j];
                    cell.className = 'cell';
                    if (board[i][j] > 0) cell.classList.add(`tile-${board[i][j]}`);
                }
            }
        }

        function updateScore(value) {
            score += value;
            document.getElementById('score').textContent = score;
            if (score > highScore) {
                highScore = score;
                document.getElementById('high-score').textContent = highScore;
                localStorage.setItem('highScore', highScore);
            }
        }

        function isGameOver() {
            for (let i = 0; i < size; i++) {
                for (let j = 0; j < size; j++) {
                    if (board[i][j] === 0) return false;
                    if (j < size - 1 && board[i][j] === board[i][j + 1]) return false;
                    if (i < size - 1 && board[i][j] === board[i + 1][j]) return false;
                }
            }
            return true;
        }

        function moveTiles(direction) {
            let moved = false;
            const oldBoard = JSON.parse(JSON.stringify(board));

            if (direction === 'up') {
                for (let j = 0; j < size; j++) {
                    for (let i = 1; i < size; i++) {
                        if (board[i][j] !== 0) {
                            let k = i;
                            while (k > 0 && board[k - 1][j] === 0) {
                                board[k - 1][j] = board[k][j];
                                board[k][j] = 0;
                                k--;
                                moved = true;
                            }
                            if (k > 0 && board[k - 1][j] === board[k][j]) {
                                board[k - 1][j] *= 2;
                                updateScore(board[k - 1][j]);
                                board[k][j] = 0;
                                moved = true;
                            }
                        }
                    }
                }
            } else if (direction === 'down') {
                for (let j = 0; j < size; j++) {
                    for (let i = size - 2; i >= 0; i--) {
                        if (board[i][j] !== 0) {
                            let k = i;
                            while (k < size - 1 && board[k + 1][j] === 0) {
                                board[k + 1][j] = board[k][j];
                                board[k][j] = 0;
                                k++;
                                moved = true;
                            }
                            if (k < size - 1 && board[k + 1][j] === board[k][j]) {
                                board[k + 1][j] *= 2;
                                updateScore(board[k + 1][j]);
                                board[k][j] = 0;
                                moved = true;
                            }
                        }
                    }
                }
            } else if (direction === 'left') {
                for (let i = 0; i < size; i++) {
                    for (let j = 1; j < size; j++) {
                        if (board[i][j] !== 0) {
                            let k = j;
                            while (k > 0 && board[i][k - 1] === 0) {
                                board[i][k - 1] = board[i][k];
                                board[i][k] = 0;
                                k--;
                                moved = true;
                            }
                            if (k > 0 && board[i][k - 1] === board[i][k]) {
                                board[i][k - 1] *= 2;
                                updateScore(board[i][k - 1]);
                                board[i][k] = 0;
                                moved = true;
                            }
                        }
                    }
                }
            } else if (direction === 'right') {
                for (let i = 0; i < size; i++) {
                    for (let j = size - 2; j >= 0; j--) {
                        if (board[i][j] !== 0) {
                            let k = j;
                            while (k < size - 1 && board[i][k + 1] === 0) {
                                board[i][k + 1] = board[i][k];
                                board[i][k] = 0;
                                k++;
                                moved = true;
                            }
                            if (k < size - 1 && board[i][k + 1] === board[i][k]) {
                                board[i][k + 1] *= 2;
                                updateScore(board[i][k + 1]);
                                board[i][k] = 0;
                                moved = true;
                            }
                        }
                    }
                }
            }

            if (moved) {
                addRandomTile();
                updateBoard();
                if (isGameOver()) {
                    setTimeout(() => alert(`Game Over! Your score: ${score}`), 100);
                }
            }
        }

        document.addEventListener('keydown', (event) => {
            switch (event.key) {
                case 'ArrowUp': moveTiles('up'); break;
                case 'ArrowDown': moveTiles('down'); break;
                case 'ArrowLeft': moveTiles('left'); break;
                case 'ArrowRight': moveTiles('right'); break;
            }
        });

        document.getElementById('restart').addEventListener('click', () => {
            board = Array(size).fill().map(() => Array(size).fill(0));
            score = 0;
            document.getElementById('score').textContent = 0;
            grid.innerHTML = '';
            initializeBoard();
        });

        initializeBoard();
    </script>
</body>
</html>
